mapper.select:
select

from com_balance
where com_uni_code = #{comUniCode,jdbcType=BIGINT} order by end_date desc limit #{limit,jdbcType=INTEGER}

select

from com_balance
where 1=1

  ${reporttype}


  and consolidation = #{consolidation}


  and report_format = #{report_format}


  AND end_date >= #{start_time}


  AND end_date <= #{end_time}


  AND end_date = #{end_date}


  and com_uni_code = #{com_uni_code}


  order by end_date  ${orderby}


  LIMIT ${limit} OFFSET ${offset}
